import 'package:flutter/material.dart';\nimport '../models/hotel.dart';\nimport '../services/hotel_service.dart';\nimport '../widgets/hotel_card.dart';\n\nclass HomeScreen extends StatefulWidget {\n  const HomeScreen({super.key});\n\n  @override\n  State<HomeScreen> createState() => _HomeScreenState();\n}\n\nclass _HomeScreenState extends State<HomeScreen> {\n  List<Hotel> _hotels = [];\n  List<Hotel> _filteredHotels = [];\n  final TextEditingController _searchController = TextEditingController();\n\n  @override\n  void initState() {\n    super.initState();\n    _loadHotels();\n  }\n\n  void _loadHotels() {\n    setState(() {\n      _hotels = HotelService.getAllHotels();\n      _filteredHotels = _hotels;\n    });\n  }\n\n  void _searchHotels(String query) {\n    setState(() {\n      if (query.isEmpty) {\n        _filteredHotels = _hotels;\n      } else {\n        _filteredHotels = HotelService.searchHotels(query);\n      }\n    });\n  }\n\n  @override\n  void dispose() {\n    _searchController.dispose();\n    super.dispose();\n  }\n\n  @override\n  Widget build(BuildContext context) {\n    return Scaffold(\n      appBar: AppBar(\n        title: const Text('Hotel Booking'),\n        actions: [\n          IconButton(\n            icon: const Icon(Icons.bookmark_border),\n            onPressed: () {\n              Navigator.pushNamed(context, '/my-bookings');\n            },\n            tooltip: 'My Bookings',\n          ),\n        ],\n      ),\n      body: Column(\n        children: [\n          // Search Bar\n          Container(\n            padding: const EdgeInsets.all(16),\n            child: TextField(\n              controller: _searchController,\n              onChanged: _searchHotels,\n              decoration: InputDecoration(\n                hintText: 'Search hotels by name or location...',\n                prefixIcon: const Icon(Icons.search),\n                suffixIcon: _searchController.text.isNotEmpty\n                    ? IconButton(\n                        icon: const Icon(Icons.clear),\n                        onPressed: () {\n                          _searchController.clear();\n                          _searchHotels('');\n                        },\n                      )\n                    : null,\n                border: OutlineInputBorder(\n                  borderRadius: BorderRadius.circular(12),\n                ),\n                filled: true,\n                fillColor: Colors.grey[100],\n              ),\n            ),\n          ),\n          // Header\n          Padding(\n            padding: const EdgeInsets.symmetric(horizontal: 16, vertical: 8),\n            child: Row(\n              mainAxisAlignment: MainAxisAlignment.spaceBetween,\n              children: [\n                Text(\n                  'Available Hotels',\n                  style: Theme.of(context).textTheme.titleLarge?.copyWith(\n                        fontWeight: FontWeight.bold,\n                      ),\n                ),\n                Text(\n                  '${_filteredHotels.length} hotels',\n                  style: Theme.of(context).textTheme.bodyMedium?.copyWith(\n                        color: Colors.grey[600],\n                      ),\n                ),\n              ],\n            ),\n          ),\n          // Hotels List\n          Expanded(\n            child: _filteredHotels.isEmpty\n                ? Center(\n                    child: Column(\n                      mainAxisAlignment: MainAxisAlignment.center,\n                      children: [\n                        Icon(\n                          Icons.search_off,\n                          size: 64,\n                          color: Colors.grey[400],\n                        ),\n                        const SizedBox(height: 16),\n                        Text(\n                          'No hotels found',\n                          style: TextStyle(\n                            fontSize: 18,\n                            color: Colors.grey[600],\n                          ),\n                        ),\n                      ],\n                    ),\n                  )\n                : ListView.builder(\n                    padding: const EdgeInsets.all(16),\n                    itemCount: _filteredHotels.length,\n                    itemBuilder: (context, index) {\n                      return HotelCard(\n                        hotel: _filteredHotels[index],\n                        onTap: () {\n                          Navigator.pushNamed(\n                            context,\n                            '/hotel-details',\n                            arguments: _filteredHotels[index],\n                          );\n                        },\n                      );\n                    },\n                  ),\n          ),\n        ],\n      ),\n    );\n  }\n}