import '../models/booking.dart';\n\nclass BookingService {\n  // Mock in-memory storage for bookings\n  static final List<Booking> _bookings = [];\n\n  static List<Booking> getAllBookings() {\n    return List.from(_bookings);\n  }\n\n  static void createBooking(Booking booking) {\n    _bookings.add(booking);\n  }\n\n  static void cancelBooking(String bookingId) {\n    final index = _bookings.indexWhere((b) => b.id == bookingId);\n    if (index != -1) {\n      final booking = _bookings[index];\n      _bookings[index] = Booking(\n        id: booking.id,\n        hotelId: booking.hotelId,\n        hotelName: booking.hotelName,\n        hotelImage: booking.hotelImage,\n        location: booking.location,\n        checkInDate: booking.checkInDate,\n        checkOutDate: booking.checkOutDate,\n        guests: booking.guests,\n        rooms: booking.rooms,\n        totalPrice: booking.totalPrice,\n        guestName: booking.guestName,\n        guestEmail: booking.guestEmail,\n        guestPhone: booking.guestPhone,\n        status: BookingStatus.cancelled,\n        bookingDate: booking.bookingDate,\n      );\n    }\n  }\n\n  static Booking? getBookingById(String id) {\n    try {\n      return _bookings.firstWhere((booking) => booking.id == id);\n    } catch (e) {\n      return null;\n    }\n  }\n}