import '../models/hotel.dart';\n\nclass HotelService {\n  // Mock data for hotels - in production this would fetch from a backend/database\n  static List<Hotel> getAllHotels() {\n    return [\n      Hotel(\n        id: '1',\n        name: 'Grand Luxury Hotel',\n        location: 'New York, USA',\n        imageUrl: 'https://images.unsplash.com/photo-1566073771259-6a8506099945?w=800',\n        rating: 4.8,\n        reviewCount: 1234,\n        pricePerNight: 250.0,\n        description: 'Experience luxury at its finest in the heart of Manhattan. Our 5-star hotel offers world-class amenities, stunning views, and exceptional service.',\n        amenities: ['Free WiFi', 'Pool', 'Spa', 'Restaurant', 'Gym', 'Room Service', 'Parking'],\n        images: [\n          'https://images.unsplash.com/photo-1566073771259-6a8506099945?w=800',\n          'https://images.unsplash.com/photo-1582719478250-c89cae4dc85b?w=800',\n          'https://images.unsplash.com/photo-1590490360182-c33d57733427?w=800',\n        ],\n      ),\n      Hotel(\n        id: '2',\n        name: 'Seaside Resort & Spa',\n        location: 'Miami Beach, USA',\n        imageUrl: 'https://images.unsplash.com/photo-1520250497591-112f2f40a3f4?w=800',\n        rating: 4.6,\n        reviewCount: 856,\n        pricePerNight: 180.0,\n        description: 'Beachfront paradise with crystal-clear ocean views. Relax and rejuvenate at our full-service spa while enjoying the sound of waves.',\n        amenities: ['Beach Access', 'Pool', 'Spa', 'Restaurant', 'Bar', 'Water Sports'],\n        images: [\n          'https://images.unsplash.com/photo-1520250497591-112f2f40a3f4?w=800',\n          'https://images.unsplash.com/photo-1571896349842-33c89424de2d?w=800',\n          'https://images.unsplash.com/photo-1571003123894-1f0594d2b5d9?w=800',\n        ],\n      ),\n      Hotel(\n        id: '3',\n        name: 'Mountain View Lodge',\n        location: 'Aspen, Colorado',\n        imageUrl: 'https://images.unsplash.com/photo-1542314831-068cd1dbfeeb?w=800',\n        rating: 4.7,\n        reviewCount: 642,\n        pricePerNight: 200.0,\n        description: 'Cozy mountain retreat with breathtaking alpine views. Perfect for ski enthusiasts and nature lovers seeking adventure and relaxation.',\n        amenities: ['Ski Storage', 'Fireplace', 'Restaurant', 'Bar', 'Gym', 'Hot Tub'],\n        images: [\n          'https://images.unsplash.com/photo-1542314831-068cd1dbfeeb?w=800',\n          'https://images.unsplash.com/photo-1564501049412-61c2a3083791?w=800',\n          'https://images.unsplash.com/photo-1551882547-ff40c63fe5fa?w=800',\n        ],\n      ),\n      Hotel(\n        id: '4',\n        name: 'Downtown Business Hotel',\n        location: 'San Francisco, USA',\n        imageUrl: 'https://images.unsplash.com/photo-1551882547-ff40c63fe5fa?w=800',\n        rating: 4.5,\n        reviewCount: 923,\n        pricePerNight: 160.0,\n        description: 'Modern business hotel in the financial district. Perfect for business travelers with conference facilities and high-speed internet.',\n        amenities: ['Free WiFi', 'Business Center', 'Gym', 'Restaurant', 'Meeting Rooms'],\n        images: [\n          'https://images.unsplash.com/photo-1551882547-ff40c63fe5fa?w=800',\n          'https://images.unsplash.com/photo-1561501900-3701fa6a0864?w=800',\n          'https://images.unsplash.com/photo-1578683010236-d716f9a3f461?w=800',\n        ],\n      ),\n      Hotel(\n        id: '5',\n        name: 'Tropical Paradise Resort',\n        location: 'Honolulu, Hawaii',\n        imageUrl: 'https://images.unsplash.com/photo-1571003123894-1f0594d2b5d9?w=800',\n        rating: 4.9,\n        reviewCount: 1567,\n        pricePerNight: 320.0,\n        description: 'Ultimate tropical getaway with pristine beaches and lush gardens. Indulge in island luxury with our award-winning restaurants and spa.',\n        amenities: ['Beach Access', 'Pool', 'Spa', 'Multiple Restaurants', 'Golf Course', 'Kids Club'],\n        images: [\n          'https://images.unsplash.com/photo-1571003123894-1f0594d2b5d9?w=800',\n          'https://images.unsplash.com/photo-1582719478250-c89cae4dc85b?w=800',\n          'https://images.unsplash.com/photo-1596436889106-be35e843f974?w=800',\n        ],\n      ),\n      Hotel(\n        id: '6',\n        name: 'Historic Boutique Inn',\n        location: 'Boston, USA',\n        imageUrl: 'https://images.unsplash.com/photo-1578683010236-d716f9a3f461?w=800',\n        rating: 4.4,\n        reviewCount: 412,\n        pricePerNight: 140.0,\n        description: 'Charming historic property with modern amenities. Located in the heart of historic Boston, walking distance to major attractions.',\n        amenities: ['Free WiFi', 'Restaurant', 'Bar', 'Concierge', 'Room Service'],\n        images: [\n          'https://images.unsplash.com/photo-1578683010236-d716f9a3f461?w=800',\n          'https://images.unsplash.com/photo-1564501049412-61c2a3083791?w=800',\n          'https://images.unsplash.com/photo-1566073771259-6a8506099945?w=800',\n        ],\n      ),\n    ];\n  }\n\n  static Hotel? getHotelById(String id) {\n    try {\n      return getAllHotels().firstWhere((hotel) => hotel.id == id);\n    } catch (e) {\n      return null;\n    }\n  }\n\n  static List<Hotel> searchHotels(String query) {\n    final lowerQuery = query.toLowerCase();\n    return getAllHotels().where((hotel) {\n      return hotel.name.toLowerCase().contains(lowerQuery) ||\n          hotel.location.toLowerCase().contains(lowerQuery);\n    }).toList();\n  }\n}