import 'package:flutter/material.dart';\nimport '../models/booking.dart';\n\nclass BookingCard extends StatelessWidget {\n  final Booking booking;\n  final VoidCallback onCancel;\n\n  const BookingCard({\n    super.key,\n    required this.booking,\n    required this.onCancel,\n  });\n\n  Color _getStatusColor() {\n    switch (booking.status) {\n      case BookingStatus.confirmed:\n        return Colors.green;\n      case BookingStatus.pending:\n        return Colors.orange;\n      case BookingStatus.cancelled:\n        return Colors.red;\n    }\n  }\n\n  String _getStatusText() {\n    switch (booking.status) {\n      case BookingStatus.confirmed:\n        return 'Confirmed';\n      case BookingStatus.pending:\n        return 'Pending';\n      case BookingStatus.cancelled:\n        return 'Cancelled';\n    }\n  }\n\n  @override\n  Widget build(BuildContext context) {\n    return Card(\n      margin: const EdgeInsets.only(bottom: 16),\n      child: Padding(\n        padding: const EdgeInsets.all(12),\n        child: Column(\n          crossAxisAlignment: CrossAxisAlignment.start,\n          children: [\n            // Header with status badge\n            Row(\n              mainAxisAlignment: MainAxisAlignment.spaceBetween,\n              children: [\n                Text(\n                  'Booking #${booking.id.substring(booking.id.length - 6)}',\n                  style: const TextStyle(\n                    fontWeight: FontWeight.bold,\n                    fontSize: 12,\n                    color: Colors.grey,\n                  ),\n                ),\n                Container(\n                  padding: const EdgeInsets.symmetric(\n                    horizontal: 8,\n                    vertical: 4,\n                  ),\n                  decoration: BoxDecoration(\n                    color: _getStatusColor().withOpacity(0.1),\n                    borderRadius: BorderRadius.circular(8),\n                    border: Border.all(color: _getStatusColor()),\n                  ),\n                  child: Text(\n                    _getStatusText(),\n                    style: TextStyle(\n                      color: _getStatusColor(),\n                      fontWeight: FontWeight.bold,\n                      fontSize: 12,\n                    ),\n                  ),\n                ),\n              ],\n            ),\n            const SizedBox(height: 12),\n            // Hotel info\n            Row(\n              children: [\n                ClipRRect(\n                  borderRadius: BorderRadius.circular(8),\n                  child: Image.network(\n                    booking.hotelImage,\n                    width: 80,\n                    height: 80,\n                    fit: BoxFit.cover,\n                    errorBuilder: (context, error, stackTrace) {\n                      return Container(\n                        width: 80,\n                        height: 80,\n                        color: Colors.grey[300],\n                        child: const Icon(Icons.hotel),\n                      );\n                    },\n                  ),\n                ),\n                const SizedBox(width: 12),\n                Expanded(\n                  child: Column(\n                    crossAxisAlignment: CrossAxisAlignment.start,\n                    children: [\n                      Text(\n                        booking.hotelName,\n                        style: const TextStyle(\n                          fontWeight: FontWeight.bold,\n                          fontSize: 16,\n                        ),\n                        maxLines: 2,\n                        overflow: TextOverflow.ellipsis,\n                      ),\n                      const SizedBox(height: 4),\n                      Row(\n                        children: [\n                          Icon(Icons.location_on,\n                              size: 14, color: Colors.grey[600]),\n                          const SizedBox(width: 2),\n                          Expanded(\n                            child: Text(\n                              booking.location,\n                              style: TextStyle(\n                                color: Colors.grey[600],\n                                fontSize: 12,\n                              ),\n                              maxLines: 1,\n                              overflow: TextOverflow.ellipsis,\n                            ),\n                          ),\n                        ],\n                      ),\n                    ],\n                  ),\n                ),\n              ],\n            ),\n            const SizedBox(height: 12),\n            const Divider(),\n            const SizedBox(height: 8),\n            // Booking details\n            Row(\n              children: [\n                Expanded(\n                  child: Column(\n                    crossAxisAlignment: CrossAxisAlignment.start,\n                    children: [\n                      Text(\n                        'Check-in',\n                        style: TextStyle(\n                          color: Colors.grey[600],\n                          fontSize: 12,\n                        ),\n                      ),\n                      const SizedBox(height: 2),\n                      Text(\n                        '${booking.checkInDate.day}/${booking.checkInDate.month}/${booking.checkInDate.year}',\n                        style: const TextStyle(\n                          fontWeight: FontWeight.bold,\n                        ),\n                      ),\n                    ],\n                  ),\n                ),\n                const Icon(Icons.arrow_forward, size: 16),\n                Expanded(\n                  child: Column(\n                    crossAxisAlignment: CrossAxisAlignment.start,\n                    children: [\n                      Text(\n                        'Check-out',\n                        style: TextStyle(\n                          color: Colors.grey[600],\n                          fontSize: 12,\n                        ),\n                      ),\n                      const SizedBox(height: 2),\n                      Text(\n                        '${booking.checkOutDate.day}/${booking.checkOutDate.month}/${booking.checkOutDate.year}',\n                        style: const TextStyle(\n                          fontWeight: FontWeight.bold,\n                        ),\n                      ),\n                    ],\n                  ),\n                ),\n              ],\n            ),\n            const SizedBox(height: 12),\n            Row(\n              mainAxisAlignment: MainAxisAlignment.spaceBetween,\n              children: [\n                Row(\n                  children: [\n                    Icon(Icons.person, size: 16, color: Colors.grey[600]),\n                    const SizedBox(width: 4),\n                    Text('${booking.guests} guest(s)'),\n                    const SizedBox(width: 16),\n                    Icon(Icons.hotel, size: 16, color: Colors.grey[600]),\n                    const SizedBox(width: 4),\n                    Text('${booking.rooms} room(s)'),\n                  ],\n                ),\n                Text(\n                  '${booking.numberOfNights} night(s)',\n                  style: TextStyle(color: Colors.grey[600]),\n                ),\n              ],\n            ),\n            const SizedBox(height: 12),\n            const Divider(),\n            const SizedBox(height: 8),\n            // Total and action\n            Row(\n              mainAxisAlignment: MainAxisAlignment.spaceBetween,\n              children: [\n                Column(\n                  crossAxisAlignment: CrossAxisAlignment.start,\n                  children: [\n                    Text(\n                      'Total Price',\n                      style: TextStyle(\n                        color: Colors.grey[600],\n                        fontSize: 12,\n                      ),\n                    ),\n                    Text(\n                      '\$${booking.totalPrice.toStringAsFixed(2)}',\n                      style: const TextStyle(\n                        fontSize: 20,\n                        fontWeight: FontWeight.bold,\n                        color: Colors.blue,\n                      ),\n                    ),\n                  ],\n                ),\n                if (booking.status == BookingStatus.confirmed)\n                  OutlinedButton.icon(\n                    onPressed: onCancel,\n                    icon: const Icon(Icons.cancel_outlined, size: 16),\n                    label: const Text('Cancel'),\n                    style: OutlinedButton.styleFrom(\n                      foregroundColor: Colors.red,\n                      side: const BorderSide(color: Colors.red),\n                    ),\n                  ),\n              ],\n            ),\n          ],\n        ),\n      ),\n    );\n  }\n}